import React, { useState, useEffect } from "react";
function UseFetch() {
    
  const [room, setRoom] = useState([]);


	useEffect(() => {
		fetch(`http://127.0.0.1:8000/api/apirooms`)
			.then(res => res.json())
			.then(data => setRoom(data));
	});


return(
     <div className="row mb_30">
                    <div className="col-lg-3 col-sm-6">


                        {room.map((rooms) =>
                        <div className="accomodation_item text-center">
                            <div className="hotel_img">
                                <img src="assets/image/room1.jpg" alt=""/>
                                <a href="/" className="btn theme_btn button_hover">Book Now</a>
                            </div>
                            <a href="/"><h4 className="sec_h4">{rooms.room_name}</h4></a>
                            <h5>{rooms.room_price}<small>/night</small></h5>
                        </div>   )}
                    </div>
   </div>
  
)
}

export default UseFetch;
